import React from "react";
import { Button, Modal, ModalBody, ModalHeader, ModalFooter } from "mdbreact";

export default class AddDevices extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showModal: false,
            name: this.props.blueprintData.name,
            uuid: this.props.blueprintData.uuid,
            comment: this.props.blueprintData.comment,
            commands: this.props.blueprintData.commands,
            sensors: this.props.blueprintData.sensors,
            attributes: this.props.blueprintData.attributes
        };
    }

    toggle() {
        this.setState({
            showModal: !this.state.showModal
        });
    }

    render() {
        let { commands, attributes, sensors, name, comment } = this.state;
        console.log(this.props);

        return (
            <div className="createModal-component">
                <Button color="blue" onClick={() => this.toggle()}>
                    Add Devices
                </Button>
                <Modal
                    isOpen={this.state.showModal}
                    toggle={() => this.toggle()}
                    size="fluid"
                >
                    <ModalHeader toggle={() => this.toggle()}>
                        Details for Blueprint: <span id="bp-font">{name}</span>
                    </ModalHeader>
                    <ModalBody />
                    <ModalFooter>
                        <Button color="primary" onClick={() => this.toggle()}>
                            Back to Blueprint
                        </Button>
                    </ModalFooter>
                </Modal>
            </div>
        );
    }
}
