import React from "react";
import uuidv4 from "uuid/v4";
import { Button, Modal, ModalBody, ModalHeader, ModalFooter } from "mdbreact";

export default class CreateModal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showModal: false,
            name: "",
            blueprint_uuid: uuidv4(),
            comment: "placeholder",
            commands: [{ name: "command" }],
            sensors: [],
            attributes: {}
        };
        this.addCommand = this.addCommand.bind(this);
    }

    toggle() {
        this.setState({
            showModal: !this.state.showModal,
            commands: [{ name: "command" }]
        });
    }

    addCommand() {
        this.setState({
            commands: [...this.state.commands, { name: "command" }]
        });
    }

    handleChange(e) {}

    render() {
        let { commands } = this.state;
        let { updateBlueprints } = this.props;

        let commandsHtml = commands.map((val, idx) => {
            let nameId = `name-${idx}`,
                commandId = `command-${idx}`;
            return (
                <div key={idx}>
                    <label htmlFor={nameId}>{`Name #${idx + 1}`}</label>
                    <input type="text" name={nameId} data-id={idx} />
                    <label htmlFor={commandId}>{`Command #${idx + 1}`}</label>
                    <input type="text" name={commandId} data-id={idx} />
                </div>
            );
        });

        return (
            <div className="createModal-component">
                <Button color="primary" onClick={() => this.toggle()}>
                    Create
                </Button>
                <Modal
                    isOpen={this.state.showModal}
                    toggle={() => this.toggle()}
                    size="fluid"
                >
                    <ModalHeader toggle={() => this.toggle()}>
                        Blueprint
                    </ModalHeader>
                    <ModalBody>
                        <div>
                            <label htmlFor="name">Name</label>
                            <input name="name" onChange={this.handleChange} />
                        </div>
                        <div>
                            <label htmlFor="comment">Comment</label>
                            <input name="comment" />
                        </div>
                        <div>
                            <p>Commands</p>
                            <Button color="green" onClick={this.addCommand}>
                                Add Command
                            </Button>
                            {commandsHtml}
                        </div>
                        <div>
                            <p>Sensors</p>
                            <Button color="green">Add Sensors</Button>
                        </div>
                        <div>
                            <p>Attributes</p>
                            <Button color="green">Add Attributes</Button>
                        </div>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="secondary" onClick={() => this.toggle()}>
                            Close
                        </Button>
                        <Button
                            color="primary"
                            onClick={() => {
                                this.toggle();
                                updateBlueprints(this.state);
                            }}
                        >
                            Create Blueprint
                        </Button>
                    </ModalFooter>
                </Modal>
            </div>
        );
    }
}
