import React from "react";
import { Button, Modal, ModalBody, ModalHeader, ModalFooter } from "mdbreact";

export default class CreateModal extends React.Component {
    constructor(props) {
        super(props);
        this.state = { showModal: false };
    }

    toggle() {
        this.setState({
            showModal: !this.state.showModal
        });
    }

    render() {
        const { updateBlueprints, handleChange } = this.props;

        return (
            <div className="createModal-component">
                <Button color="primary" onClick={() => this.toggle()}>
                    Create
                </Button>
                <Modal
                    isOpen={this.state.showModal}
                    toggle={() => this.toggle()}
                    size="fluid"
                >
                    <ModalHeader toggle={() => this.toggle()}>
                        Blueprint
                    </ModalHeader>
                    <ModalBody>
                        <form>
                            <div>
                                <label htmlFor="name">Name</label>
                                <input name="name" onChange={handleChange} />
                            </div>
                            <div>
                                <label htmlFor="comment">Comment</label>
                                <input name="comment" />
                            </div>
                            <div>
                                <p>Commands</p>
                                <Button color="green">Add Command</Button>
                            </div>
                            <div>
                                <p>Sensors</p>
                                <Button color="green">Add Sensors</Button>
                            </div>
                            <div>
                                <p>Attributes</p>
                                <Button color="green">Add Attributes</Button>
                            </div>
                        </form>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="secondary" onClick={() => this.toggle()}>
                            Close
                        </Button>
                        <Button
                            color="primary"
                            onClick={() => {
                                this.toggle();
                                updateBlueprints();
                            }}
                        >
                            Create Blueprint
                        </Button>
                    </ModalFooter>
                </Modal>
            </div>
        );
    }
}
