import React from "react";
import { Button, Modal, ModalBody, ModalHeader, ModalFooter } from "mdbreact";

import CommandsInput from "./CommandsInput";

export default class CreateModal extends React.Component {
    constructor(props) {
        super(props);
        //form input data gets populated in THIS state and sent to parent state
        this.state = {
            showModal: false,
            name: "",
            comment: "",
            commands: [{ name: "", command: "" }],
            sensors: [],
            attributes: {}
        };
        this.handleChange = this.handleChange.bind(this);
        this.addInputField = this.addInputField.bind(this);
    }

    toggle() {
        this.setState({
            showModal: !this.state.showModal,
            commands: [{ name: "command" }]
        });
    }

    addInputField(capability) {
        console.log("capability", capability);
        this.setState({
            commands: [...this.state.commands, { name: "command" }]
        });
    }

    handleChange(e) {
        if (["command-name", "command-id"].includes(e.target.className)) {
            let commands = [...this.state.commands];
            console.log(e.target);
        }
    }

    render() {
        // console.log(this.state);
        let { commands } = this.state;
        let { updateBlueprints } = this.props;

        return (
            <div className="createModal-component">
                <Button color="primary" onClick={() => this.toggle()}>
                    Create
                </Button>
                <Modal
                    isOpen={this.state.showModal}
                    toggle={() => this.toggle()}
                    size="fluid"
                >
                    <ModalHeader toggle={() => this.toggle()}>
                        Blueprint
                    </ModalHeader>
                    <ModalBody>
                        <div>
                            <label htmlFor="blueprint-name">Name</label>
                            <input
                                name="blueprint-name"
                                onChange={e => {
                                    this.setState({ name: e.target.value });
                                }}
                            />
                        </div>
                        <div>
                            <label htmlFor="comment">Comment</label>
                            <input
                                name="comment"
                                onChange={e => {
                                    this.setState({ comment: e.target.value });
                                }}
                            />
                        </div>
                        <div>
                            <p>Commands</p>
                            <Button
                                color="green"
                                onClick={this.addInputField(commands)}
                            >
                                Add Command
                            </Button>
                            <CommandsInput commands={commands} />
                        </div>
                        <div>
                            <p>Sensors</p>
                            <Button color="green">Add Sensors</Button>
                        </div>
                        <div>
                            <p>Attributes</p>
                            <Button color="green">Add Attributes</Button>
                        </div>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="secondary" onClick={() => this.toggle()}>
                            Close
                        </Button>
                        <Button
                            color="primary"
                            onClick={() => {
                                this.toggle();
                                updateBlueprints(this.state);
                            }}
                        >
                            Create Blueprint
                        </Button>
                    </ModalFooter>
                </Modal>
            </div>
        );
    }
}
