import React from "react";
import { Button, Modal, ModalBody, ModalHeader, ModalFooter } from "mdbreact";

import Device from "./Device";

export default class DevicesOverview extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            ...this.props.blueprintData,
            devices: [
                {
                    identifier: "Berlin Mitte",
                    owner: "john@doe.com",
                    last_act: "No activities yet",
                    self_reg: "false"
                }
            ]
        };
        this.handleChange = this.handleChange.bind(this);
        // this.addDevice = this.addDevice.bind(this);
    }

    toggle() {
        this.setState({
            showModal: !this.state.showModal
        });
    }

    handleChange(e) {
        addDevice(e.target.value);
    }

    addDevice(str) {
        this.setState({ devices: [...this.state.devices, e.target.value] });
    }

    render() {
        let { commands, attributes, sensors, name, comment } = this.state;

        return (
            <div className="createModal-component">
                <Button color="blue" onClick={() => this.toggle()}>
                    Add Devices
                </Button>
                <Modal
                    isOpen={this.state.showModal}
                    toggle={() => this.toggle()}
                    size="fluid"
                >
                    <ModalHeader toggle={() => this.toggle()}>
                        Details for Blueprint:<span id="bp-font"> {name}</span>
                    </ModalHeader>
                    <ModalBody>
                        <h5>Register device</h5>
                        <input name="register" onChange={this.handleChange} />
                        <Button color="green" onClick={this.handleChange}>
                            Register
                        </Button>
                        <Device
                            devices={this.state.devices}
                            attributes={attributes}
                        />
                    </ModalBody>
                    <ModalFooter>
                        <Button color="primary" onClick={() => this.toggle()}>
                            Back to Blueprint
                        </Button>
                    </ModalFooter>
                </Modal>
            </div>
        );
    }
}
