import React from "react";

import CreateModal from "./CreateModal";
import Blueprint from "./Blueprint";
import { examples } from "./examples";

export default class BlueprintOverview extends React.Component {
    constructor() {
        super();
        this.state = { blueprints: [] };
        this.updateBlueprints = this.updateBlueprints.bind(this);
    }

    componentDidMount() {
        //Here goes a GET call to server for all existing blueprints.
        this.setState({
            blueprints: examples
        });
    }

    updateBlueprints() {
        const blueprintInput = {
            name: "placeholder",
            blueprint_uuid: "placeholder",
            comment: "placeholder"
        };
        console.log("boom!", this);

        //Here goes an ajax call to server to add new blueprints data.
    }

    render() {
        console.log("BlueprintOverview Component state -> ", this.state);

        let allBlueprints = this.state.blueprints.map(i => {
            return <Blueprint key={i.blueprint_uuid} blueprintData={i} />;
        });

        return (
            <div className="blueprint-overview-component">
                <h1>BLUEPRINTS</h1> <h2>MANAGE YOUR DEVICE BLUEPRINTS</h2>
                <CreateModal updateBlueprints={this.updateBlueprints} />
                <div className="blueprints-flex-container">{allBlueprints}</div>
            </div>
        );
    }
}
