import React from "react";
import { Button, Modal, ModalBody, ModalHeader, ModalFooter } from "mdbreact";

export default class ModalPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = { showModal: false };
    }

    toggle() {
        this.setState({
            showModal: !this.state.showModal
        });
    }

    render() {
        return (
            <div>
                <Button color="primary" onClick={() => this.toggle()}>
                    Create
                </Button>
                <Modal
                    isOpen={this.state.showModal}
                    toggle={() => this.toggle()}
                    size="fluid"
                >
                    <ModalHeader toggle={() => this.toggle()}>
                        Blueprint
                    </ModalHeader>
                    <ModalBody>
                        <div>
                            <p>Name</p>
                            <input />
                        </div>
                        <div>
                            <p>Comment</p>
                            <input />
                        </div>
                        <div>
                            <p>Commands</p>
                            <input />
                        </div>
                        <div>
                            <p>Sensors</p>
                            <input />
                        </div>
                        <div>
                            <p>Attributes</p>
                            <input />
                        </div>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="secondary" onClick={() => this.toggle()}>
                            Close
                        </Button>
                        <Button
                            color="primary"
                            onClick={() => {
                                this.toggle();
                                this.updateBlueprints();
                            }}
                        >
                            Create Blueprint
                        </Button>
                    </ModalFooter>
                </Modal>
            </div>
        );
    }
}
