import React from "react";
import { Button, Modal, ModalBody, ModalHeader, ModalFooter } from "mdbreact";

import Input from "./Input";

export default class CreateModal extends React.Component {
    constructor(props) {
        super(props);
        //form input data gets populated in THIS state and sent to parent state
        this.state = {
            showModal: false
        };
        this.handleChange = this.handleChange.bind(this);
        this.addField = this.addField.bind(this);
    }

    toggle() {
        this.setState({
            showModal: !this.state.showModal,
            name: "",
            comment: "",
            commands: [],
            attributes: [],
            sensors: []
        });
    }

    addField(e) {
        let placeholder;
        let objFields = { name: "", value: "" };
        if (e.target.id === "add-command") {
            placeholder = "commands";
        } else if (e.target.id === "add-attribute") {
            placeholder = "attributes";
            objFields = { name: "", value: "", data_type: "", comment: "" };
        } else {
            placeholder = "sensors";
        }
        this.setState({
            [placeholder]: [...this.state[placeholder], objFields]
        });
    }

    handleChange(e) {
        if (["command-name", "command-id"].includes(e.target.className)) {
            // let commands = [...this.state.commands];
            // console.log(e.target);
        }
    }

    render() {
        console.log(this.state);
        let { commands, attributes, sensors } = this.state;
        let { updateBlueprints } = this.props;

        return (
            <div className="createModal-component">
                <Button color="primary" onClick={() => this.toggle()}>
                    Create
                </Button>
                <Modal
                    isOpen={this.state.showModal}
                    toggle={() => this.toggle()}
                    size="fluid"
                >
                    <ModalHeader toggle={() => this.toggle()}>
                        Blueprint
                    </ModalHeader>
                    <ModalBody>
                        <div>
                            <strong>
                                <label htmlFor="blueprint-name">Name</label>
                            </strong>
                            <input
                                name="blueprint-name"
                                onChange={e => {
                                    this.setState({ name: e.target.value });
                                }}
                            />
                        </div>
                        <div>
                            <strong>
                                <label htmlFor="comment">Comment</label>
                            </strong>
                            <input
                                name="comment"
                                onChange={e => {
                                    this.setState({ comment: e.target.value });
                                }}
                            />
                        </div>
                        <div>
                            <strong>
                                <p>Commands</p>
                            </strong>
                            <Button
                                color="green"
                                id="add-command"
                                onClick={this.addField}
                            >
                                Add Command
                            </Button>
                            <Input capability={commands} type={"command"} />
                        </div>
                        <div>
                            <strong>
                                <p>Sensors</p>
                            </strong>
                            <Button
                                color="green"
                                id="add-sensor"
                                onClick={this.addField}
                            >
                                Add Sensors
                            </Button>
                            <Input capability={sensors} type={"sensor"} />
                        </div>
                        <div>
                            <strong>
                                <p>Attributes</p>
                            </strong>
                            <Button
                                color="green"
                                id="add-attribute"
                                onClick={this.addField}
                            >
                                Add Attributes
                            </Button>
                            <Input capability={attributes} type={"attribute"} />
                        </div>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="secondary" onClick={() => this.toggle()}>
                            Close
                        </Button>
                        <Button
                            color="primary"
                            onClick={() => {
                                this.toggle();
                                updateBlueprints(this.state);
                            }}
                        >
                            Create Blueprint
                        </Button>
                    </ModalFooter>
                </Modal>
            </div>
        );
    }
}
