import React from "react";
import { Button } from "mdbreact";

export default function Blueprint(props) {
    const {
        name,
        blueprint_uuid,
        commands,
        sensors,
        attributes
    } = props.blueprintData;

    function iterateProps(capability) {
        const result = "";
        const keyNames = Object.getOwnPropertyNames(capability);
        return (result = keyNames.map(name => {
            return (
                <div key={name}>
                    <strong>{name}:</strong> {attributes[name]}
                </div>
            );
        }));
    }

    const attributePropNames = Object.getOwnPropertyNames(attributes);
    const commandPropNames = Object.getOwnPropertyNames(commands);
    const attributesHtml = attributePropNames.map(name => {
        return (
            <div key={name}>
                <strong>{name}:</strong> {attributes[name]}
            </div>
        );
    });
    const commandsHtml = commandPropNames.map(name => {
        return (
            <div key={name}>
                <strong>{name}:</strong> {commands[name]}
            </div>
        );
    });
    return (
        <div className="each-blueprint">
            <h3>
                <strong>{name}</strong>
            </h3>
            <Button color="green">Edit</Button>
            <Button color="blue">Add Devices</Button>
            <Button color="red">Remove</Button>
            <div>
                <strong>Blueprint Name:</strong> {name}
            </div>
            <div>
                <strong>Blueprint UUID:</strong> {blueprint_uuid}
            </div>
            <div className="capabilities-container">
                <div className="each-capability">
                    <strong>
                        <u>
                            <p>Commands</p>
                        </u>
                    </strong>
                    {iterateProps(commands)}
                </div>
                <div className="each-capability">
                    <strong>
                        <u>
                            <p>Attributes</p>
                        </u>
                    </strong>
                    {attributesHtml}
                </div>
                <div className="each-capability">
                    <strong>
                        <u>
                            <p>Sensors</p>
                        </u>
                    </strong>
                </div>
            </div>
        </div>
    );
}
