import React from "react";
import { Button, Modal, ModalBody, ModalHeader, ModalFooter } from "mdbreact";

import Input from "./Input";

export default class CreateModal extends React.Component {
    constructor(props) {
        super(props);
        //form input data gets populated in THIS state and sent to parent state
        this.state = {
            showModal: false
        };
        this.handleChange = this.handleChange.bind(this);
    }

    toggle() {
        this.setState({
            showModal: !this.state.showModal
        });
    }

    handleChange(e) {
        if (e.target.dataset.cap === "command") {
            let commands = [...this.state.commands];
            commands[e.target.dataset.id][e.target.className] = e.target.value;
            this.setState({ commands });
        } else if (e.target.dataset.cap === "attribute") {
            let att = [...this.state.attributes];
            att[e.target.dataset.id][e.target.className] = e.target.value;
            this.setState({ attributes: att });
        } else if (e.target.dataset.cap === "sensor") {
            let sensors = [...this.state.sensors];
            sensors[e.target.dataset.id][e.target.className] = e.target.value;
            this.setState({ sensors });
        }
    }

    render() {
        let { commands, attributes, sensors } = this.state;
        let { submitBlueprint } = this.props;

        return (
            <div className="createModal-component">
                <Button color="green" onClick={() => this.toggle()}>
                    Edit
                </Button>
                <Modal
                    isOpen={this.state.showModal}
                    toggle={() => this.toggle()}
                    size="fluid"
                >
                    <ModalHeader toggle={() => this.toggle()}>
                        Blueprint
                    </ModalHeader>
                    <ModalBody>
                        <div>
                            <strong>
                                <label htmlFor="blueprint-name">Name</label>
                            </strong>
                            <input
                                name="blueprint-name"
                                onChange={e => {
                                    this.setState({ name: e.target.value });
                                }}
                            />
                        </div>
                        <div>
                            <strong>
                                <label htmlFor="comment">Comment</label>
                            </strong>
                            <input
                                name="comment"
                                onChange={e => {
                                    this.setState({ comment: e.target.value });
                                }}
                            />
                        </div>
                        <div>
                            <strong>
                                <p>Commands</p>
                            </strong>
                            <Button
                                color="green"
                                onClick={() => {
                                    this.setState({
                                        commands: [
                                            ...this.state.commands,
                                            {
                                                name: "",
                                                value: ""
                                            }
                                        ]
                                    });
                                }}
                            >
                                Add Command
                            </Button>
                            <Input
                                capability={commands}
                                type={"command"}
                                handleChange={this.handleChange}
                            />
                        </div>
                        <div>
                            <strong>
                                <p>Sensors</p>
                            </strong>
                            <Button
                                color="green"
                                onClick={() => {
                                    this.setState({
                                        sensors: [
                                            ...this.state.sensors,
                                            {
                                                name: "",
                                                value: "",
                                                data_type: "",
                                                comment: ""
                                            }
                                        ]
                                    });
                                }}
                            >
                                Add Sensors
                            </Button>
                            <Input
                                capability={sensors}
                                type={"sensor"}
                                handleChange={this.handleChange}
                            />
                        </div>
                        <div>
                            <strong>
                                <p>Attributes</p>
                            </strong>
                            <Button
                                color="green"
                                onClick={() => {
                                    this.setState({
                                        attributes: [
                                            ...this.state.attributes,
                                            {
                                                name: "",
                                                value: ""
                                            }
                                        ]
                                    });
                                }}
                            >
                                Add Attributes
                            </Button>
                            <Input
                                capability={attributes}
                                type={"attribute"}
                                handleChange={this.handleChange}
                            />
                        </div>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="secondary" onClick={() => this.toggle()}>
                            Close
                        </Button>
                        <Button
                            color="primary"
                            onClick={() => {
                                this.toggle();
                                editBlueprint(this.state);
                            }}
                        >
                            Create Blueprint
                        </Button>
                    </ModalFooter>
                </Modal>
            </div>
        );
    }
}
