import React from "react";
//creates input fields for each capability on click
export default function Input(props) {
    const { type } = props;
    return props.capability.map((val, idx) => {
        let nameId = `${type}-name-${idx}`;
        let valueId = `${type}-value-${idx}`;
        let dataTypeId = `${type}-data_type-${idx}`;
        let commentId = `${type}-comment-${idx}`;

        let sensorDataField = (
            <div className="input-container">
                <label htmlFor={dataTypeId}>{`Data Type #${idx + 1}`}</label>
                <input
                    type="text"
                    name={dataTypeId}
                    data-id={idx}
                    className={`${type}-data_type`}
                />
            </div>
        );
        let sensorCommentField = (
            <div className="input-container">
                <label htmlFor={commentId}>{`comment #${idx + 1}`}</label>
                <input
                    type="text"
                    name={commentId}
                    comment-id={idx}
                    className={`${type}-commentId`}
                />
            </div>
        );

        return (
            <div key={idx}>
                <div className="input-container">
                    <label htmlFor={nameId}>{`Name #${idx + 1}`}</label>
                    <input
                        type="text"
                        name={nameId}
                        data-id={idx}
                        className={`${type}-name`}
                    />
                </div>
                <div className="input-container">
                    <label htmlFor={valueId}>{`Value #${idx + 1}`}</label>
                    <input
                        type="text"
                        name={valueId}
                        data-id={idx}
                        className={`${type}-value`}
                    />
                </div>
                {type === "sensor" && sensorDataField}
                {type === "sensor" && sensorCommentField}
            </div>
        );
    });
}
