import React from "react";
import { Button } from "mdbreact";

export default function Blueprint(props) {
    const {
        name,
        blueprint_uuid,
        commands,
        sensors,
        attributes
    } = props.blueprintData;
    console.log(sensors);

    const sensorsHtml = sensors.map(i => {
        return (
            <div key={i.name}>
                <p>
                    {" "}
                    <strong>{i.name}:</strong> {i.sensor_id}
                </p>
            </div>
        );
    });

    return (
        <div className="each-blueprint">
            <h3>
                <strong>{name}</strong>
            </h3>
            <Button color="green">Edit</Button>
            <Button color="blue">Add Devices</Button>
            <Button color="red">Remove</Button>
            <div>
                <strong>Blueprint Name:</strong> {name}
            </div>
            <div>
                <strong>Blueprint UUID:</strong> {blueprint_uuid}
            </div>
            <div className="capabilities-container">
                <div className="each-capability">
                    <strong>
                        <u>
                            <p>Commands</p>
                        </u>
                    </strong>
                    {iterateProps(commands)}
                </div>
                <div className="each-capability">
                    <strong>
                        <u>
                            <p>Attributes</p>
                        </u>
                    </strong>
                    {iterateProps(attributes)}
                </div>
                <div className="each-capability">
                    <strong>
                        <u>
                            <p>Sensors</p>
                        </u>
                        {sensorsHtml}
                    </strong>
                </div>
            </div>
        </div>
    );
}

function iterateProps(capability) {
    const keyNames = Object.getOwnPropertyNames(capability);
    const result = keyNames.map(name => {
        return (
            <div key={name}>
                <strong>{name}:</strong> {capability[name]}
            </div>
        );
    });
    return result;
}
