import React from "react";
import { Button } from "mdbreact";

export default function Blueprint(props) {
    const {
        name,
        blueprint_uuid,
        commands,
        sensors,
        attributes
    } = props.blueprintData;
    const { deleteBlueprint } = props;

    return (
        <div className="each-blueprint">
            <h3>
                <strong>{name}</strong>
            </h3>
            <Button color="green">Edit</Button>
            <Button color="blue">Add Devices</Button>
            <Button
                color="red"
                id={blueprint_uuid}
                onClick={deleteBlueprint("yo")}
            >
                Remove
            </Button>
            <div>
                <strong>Blueprint Name:</strong> {name}
            </div>
            <div>
                <strong>Blueprint UUID:</strong> {blueprint_uuid}
            </div>
            <div className="capabilities-container">
                <div className="each-capability">
                    <strong>
                        <u>
                            <p>Commands</p>
                        </u>
                    </strong>
                    {iterateHtml(commands)}
                </div>
                <div className="each-capability">
                    <strong>
                        <u>
                            <p>Attributes</p>
                        </u>
                    </strong>
                    {iterateHtml(attributes)}
                </div>
                <div className="each-capability">
                    <strong>
                        <u>
                            <p>Sensors</p>
                        </u>
                    </strong>
                    {iterateHtml(sensors)}
                </div>
            </div>
        </div>
    );
}

function iterateHtml(capability) {
    const result = capability.map((obj, key) => {
        return (
            <div key={key}>
                <strong>{obj.name} :</strong> (key: {obj.value})
            </div>
        );
    });
    return result;
}
