import React from "react";
import { Button, Modal, ModalBody, ModalHeader, ModalFooter } from "mdbreact";

import Input from "./Input";

export default class CreateModal extends React.Component {
    constructor(props) {
        super(props);
        //form input data gets populated in THIS state and sent to parent state
        this.state = {
            showModal: false,
            name: this.props.blueprintData.name,
            uuid: this.props.blueprintData.uuid,
            comment: this.props.blueprintData.comment,
            commands: this.props.blueprintData.commands,
            sensors: this.props.blueprintData.sensors,
            attributes: this.props.blueprintData.attributes
        };
        this.handleChange = this.handleChange.bind(this);
    }

    componentWillMount() {
        this.prevState = this.state;
        console.log("component mount prevstate", this.prevState);
    }

    reset() {
        this.setState({
            name: this.prevState.name,
            comment: this.prevState.comment,
            commands: this.prevState.commands,
            sensors: this.prevState.sensors,
            attributes: this.prevState.attributes
        });
    }

    toggle() {
        this.setState({ showModal: !this.state.showModal });
    }

    handleChange(e) {
        if (e.target.dataset.cap === "command") {
            let commands = [...this.state.commands];
            commands[e.target.dataset.id][e.target.className] = e.target.value;
            this.setState({ commands });
        } else if (e.target.dataset.cap === "attribute") {
            let att = [...this.state.attributes];
            att[e.target.dataset.id][e.target.className] = e.target.value;
            this.setState({ attributes: att });
        } else if (e.target.dataset.cap === "sensor") {
            let sensors = [...this.state.sensors];
            sensors[e.target.dataset.id][e.target.className] = e.target.value;
            this.setState({ sensors });
        }
    }

    render() {
        let { commands, attributes, sensors, name, comment } = this.state;
        let { editBlueprint } = this.props;

        return (
            <div className="createModal-component">
                <Button color="green" onClick={() => this.toggle()}>
                    Edit
                </Button>
                <Modal
                    isOpen={this.state.showModal}
                    toggle={() => {
                        this.toggle();
                        this.reset();
                    }}
                    size="fluid"
                >
                    <ModalHeader
                        toggle={() => {
                            this.toggle();
                            this.reset();
                        }}
                    >
                        Edit Blueprint
                    </ModalHeader>
                    <ModalBody>
                        <div>
                            <strong>
                                <label htmlFor="blueprint-name">Name</label>
                            </strong>
                            <input
                                value={name}
                                name="blueprint-name"
                                onChange={e => {
                                    this.setState({ name: e.target.value });
                                }}
                            />
                        </div>
                        <div>
                            <strong>
                                <label htmlFor="comment">Comment</label>
                            </strong>
                            <input
                                value={comment}
                                name="comment"
                                onChange={e => {
                                    this.setState({ comment: e.target.value });
                                }}
                            />
                        </div>
                        <div>
                            <strong>
                                <p>Commands</p>
                            </strong>
                            <Button
                                color="green"
                                onClick={() => {
                                    this.setState({
                                        commands: [
                                            ...this.state.commands,
                                            {
                                                name: "",
                                                value: ""
                                            }
                                        ]
                                    });
                                }}
                            >
                                Add Command
                            </Button>
                            <Input
                                type={"command"}
                                handleChange={this.handleChange}
                                capability={commands}
                            />
                        </div>
                        <div>
                            <strong>
                                <p>Sensors</p>
                            </strong>
                            <Button
                                color="green"
                                onClick={() => {
                                    this.setState({
                                        sensors: [
                                            ...this.state.sensors,
                                            {
                                                name: "",
                                                value: "",
                                                data_type: "",
                                                comment: ""
                                            }
                                        ]
                                    });
                                }}
                            >
                                Add Sensors
                            </Button>
                            <Input
                                type={"sensor"}
                                handleChange={this.handleChange}
                                capability={sensors}
                            />
                        </div>
                        <div>
                            <strong>
                                <p>Attributes</p>
                            </strong>
                            <Button
                                color="green"
                                onClick={() => {
                                    this.setState({
                                        attributes: [
                                            ...this.state.attributes,
                                            {
                                                name: "",
                                                value: ""
                                            }
                                        ]
                                    });
                                }}
                            >
                                Add Attributes
                            </Button>
                            <Input
                                type={"attribute"}
                                handleChange={this.handleChange}
                                capability={attributes}
                            />
                        </div>
                    </ModalBody>
                    <ModalFooter>
                        <Button
                            color="secondary"
                            onClick={() => {
                                this.toggle();
                                this.reset();
                            }}
                        >
                            Close
                        </Button>
                        <Button
                            color="primary"
                            onClick={() => {
                                this.toggle();
                                editBlueprint(this.state);
                            }}
                        >
                            Edit Blueprint
                        </Button>
                    </ModalFooter>
                </Modal>
            </div>
        );
    }
}
